INSERT INTO "public"."playable_race"("id", "name", "description") VALUES (E'026e76d7-ae13-431f-be13-66187cec66b8', E'The Forgotten', E'Segregated from humanity for centuries, these once homo-sapiens have resorted to any and all means to survive the harsh void of space. Many have literally shed their skin to avoid various radiation-induced cancers, or replaced limbs and organs with their own brand of horrific cybernetics. This race of self vivisectionists have even been rumoured to worship pain.');
