INSERT INTO "public"."playable_race"("id", "name", "description", "image_url") VALUES (E'98f22f43-e36c-4b0c-9554-b13af42de556', E'Arrakkens', E'Originally a sect of libertarian frontiers-men from the southern United States, the Arrakkens are now the galaxy\'s most skilled traders and proprietors of exotic goods. Once disenfranchised with the rotting Terran establishment, they chose instead to start their own colony, and fled to the clouds of Venus where they established a sprawling baloon-city metropolis. Now they roam the galaxy, specialising in finance, trade and the colonisation of gassy planets.', E'/races/arrakkens.png');
